function handleSwitch(payload, context, switchName, statusSNSName, startName, finishName, verifyName) {
   let switchKey = Object.keys(payload).find(key => key.startsWith(switchName)) // extrai o switch que chegou no payload
   let switchState = context.get(switchName) || null;
   let actionValue = payload[switchKey] ? payload[switchKey].Action : null; // extrai o estado do switch que chegou
   let statusSNS = payload[statusSNSName] && payload[statusSNSName][switchName];
   let startState = context.get(startName) || false;
   let finishState = context.get(finishName) || false;
   let verifyState = context.get(verifyName) || false;

   switchState = actionValue || statusSNS || switchState; // adquiri o estado do switch que chegou

   if (switchState == "ON" && finishState == false) { // Condição que caracteriza o início da produção da peça
      startState = true;
   }
   else if (switchState == "OFF" && startState == true) { // condição que caracteriza o final da produção da peça
      finishState = true;
   }
   verifyState = startState == true && finishState == true ? true : false;

   if (verifyState == true) { // reseta o início o fim da produção quando a peça é produzida
      startState = false;
      finishState = false;

   }
   context.set(switchName, switchState);
   context.set(startName, startState);
   context.set(finishName, finishState);
   context.set(verifyName, verifyState);

   return {
      switchState: switchState,
      startState: startState,
      finishState: finishState,
      verifyState: verifyState
   };
}

