    for (var i = 0; i < msg.payload.length; i++) {
        // Constrói o tópico com base no elemento atual do array
        var elemento = msg.payload[i];
        var topic = "tele/connectv1_" + elemento + "/LWT";

        // Cria uma nova mensagem de saída
        var novaMensagem = {
            payload: elemento,
            topic: topic,
            action: "subscribe"
        };

        // Envia a nova mensagem para o próximo nó
        node.send(novaMensagem);
